  `img` varchar(128) DEFAULT NULL COMMENT '疆域地图',
  `culture_img` varchar(128) DEFAULT NULL COMMENT '文化代表',
  `detail_description` text COMMENT '详细介绍',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

