DROP TABLE IF EXISTS `postInterpretations`;

CREATE TABLE `postInterpretations` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `post_id` int(11) unsigned NOT NULL COMMENT '文章',
